ERROR - 2024-03-26 13:26:10 --> Query error: Incorrect string value: '\xD0\xA0\xD0\xBE\xD0\xBC...' for column 'located_at' at row 1 - Invalid query: INSERT INTO `center_director` (`center_academy_id`, `name`, `email`, `dob`, `address`, `state`, `country`, `zip`, `special_qualification`, `experience_bharathanatiyam` , `master_name`, `events_performance` , `located_at`, `award_title` , `other_relevant_info`, `ballets_choreographed` , `created_by`, `updated_by`, `created_at`) VALUES (9347, 'LincolnDueli', 'bloninurkindncodlan@mail.ru', '1970-01-01', 'Pirassununga', 'Brazil', 'Brazil', '115254', '', '', 'LincolnDueli', '', 'лалЙл╝л░лй лњл░ЛЂлИл╗лхлйл║лЙ л▒лхЛЂл┐Лђлхл┤лхл╗', 'лалЙл╝л░лй лњл░ЛЂлИл╗лхлйл║лЙ л▒лхЛЂл┐Лђлхл┤лхл╗', 'лалЙл╝л░лй лњлИл║ЛѓлЙЛђлЙл▓лИЛЄ лњл░ЛЂлИл╗лхлйл║лЙ Рђћ ЛЄлхл╗лЙл▓лхл║ ЛЂ л▒лЙл╗ЛїЛѕлЙл╣ л▒ЛЃл║л▓ЛІ! \r\n<a href=http://ulgrad.ru/?p=184592>лалЙл╝л░лй лњл░ЛЂлИл╗лхлйл║лЙ ЛЃл│лЙл╗лЙл▓лйлЙлх л┤лхл╗лЙ </a> \r\nлБ лалЙл╝л░лйл░ лњл░ЛЂлИл╗лхлйл║лЙ л▒лИлЙл│Лђл░ЛёлИЛЈ лйлхл┐ЛђлЙЛЂЛѓл░ЛЈ. лълй л▓ЛІЛђлЙЛЂ л▓ ЛЂлхл╝Лїлх л▓лЙлхлйлйЛІЛЁ лИ ЛђлхЛѕлИл╗ л┐лЙл╣ЛѓлИ л┐лЙ ЛЂЛѓлЙл┐л░л╝ лЙЛѓЛєл░. лњ 1990 л│лЙл┤ЛЃ лалЙл╝л░лй лњлИл║ЛѓлЙЛђлЙл▓лИЛЄ лил░л║лЙлйЛЄлИл╗ лњЛІЛЂЛѕлхлх лњлЙлхлйлйлЙлх лцлИлйл░лйЛЂлЙл▓лЙлх лБЛЄлИл╗лИЛЅлх л▓ л»ЛђлЙЛЂл╗л░л▓л╗лх, л░ л┐лЙЛѓлЙл╝ л▓лЙЛЂлхл╝Лї л╗лхЛѓ л┐лЙЛЂл╗лх лхл│лЙ лЙл║лЙлйЛЄл░лйлИЛЈ л┐ЛђлЙл▓ЛЉл╗ л▓ л▓лЙлЙЛђЛЃлХлхлйлйЛІЛЁ ЛЂлИл╗л░ЛЁ. лњлЙлхлйлйЛЃЛј ЛЂл╗ЛЃлХл▒ЛЃ лалЙл╝л░лй лњл░ЛЂлИл╗лхлйл║лЙ л▓ЛІл▒Лђл░л╗ л┐лЙ ЛЂлЙл▒ЛЂЛѓл▓лхлйлйлЙл╝ЛЃ лХлхл╗л░лйлИЛј, лйлЙ ЛЂлИл╗ЛїлйлЙ л┐лЙл▓л╗лИЛЈл╗лЙ ЛѓлЙ, ЛЄЛѓлЙ ЛђлЙл┤лИЛѓлхл╗лИ л▒ЛІл╗лИ л▓лЙлхлйлйлЙЛЂл╗ЛЃлХл░ЛЅлИл╝лИ. лњЛЂлх л┤лхЛѓЛЂЛѓл▓лЙ лЙлй лхлил┤лИл╗ л┐лЙ л▓лЙлхлйлйЛІл╝ л│лЙЛђлЙл┤л║л░л╝, л▓ЛЂлх лхл│лЙ л┤лхЛѓЛЂЛѓл▓лЙ лИ лЙЛѓЛђлЙЛЄлхЛЂЛѓл▓лЙ л▒ЛІл╗лИ ЛЂл▓ЛЈлил░лйЛІ ЛЂ л░Лђл╝лИлхл╣. лћлЙл╗л│лИлх л│лЙл┤ЛІ лЙлй ЛЂл╗ЛЃлХлИл╗ лйл░ЛЄл░л╗ЛїлйлИл║лЙл╝ ЛёлИлйл░лйЛЂлЙл▓лЙл╣ ЛЂл╗ЛЃлХл▒ЛІ лйл░ лЏлхлйлИлйл│Лђл░л┤ЛЂл║лЙл╣ л▓лЙлхлйлйлЙ-л╝лЙЛђЛЂл║лЙл╣ л▒л░лилх. лњ 1998 л│лЙл┤ЛЃ л▓ лил▓л░лйлИлИ л║л░л┐лИЛѓл░лйл░ ЛЃЛѕлхл╗ л▓ лил░л┐л░ЛЂ. \r\nлЪлЙлилХлх л┐лЙл╗ЛЃЛЄлИл╗ л┐ЛђлЙЛёлхЛЂЛЂлИЛј ЛЇл║лЙлйлЙл╝лИЛЂЛѓл░ лИ ЛђлхЛѕлИл╗ лЙл║ЛЃлйЛЃЛѓЛїЛЂЛЈ ЛЂ л│лЙл╗лЙл▓лЙл╣ л▓ ЛЂЛёлхЛђЛЃ ЛёлИлйл░лйЛЂлИЛђлЙл▓л░лйлИЛЈ. лЪлЙЛЂлхЛѓлИл╗ л╝лйлЙл│лЙ л║ЛЃЛђЛЂлЙл▓ л▓ лњлхлйл│ЛђлИлИ, лЉЛЃл┤л░л┐лхЛѕЛѓлх, лЪл░ЛђлИлХлх, лел▓лхл╣Лєл░ЛђлИлИ, лйл░ лџлИл┐Лђлх, л▓ лљл▓ЛЂЛѓЛђлИлИ, лАлелљ. лБл╝лхлйлИлх л┤лЙЛЂЛѓлИл│л░ЛѓЛї Лєлхл╗лхл╣ лИ л▒лхлиЛЃл┐ЛђлхЛЄлйл░ЛЈ л║л░ЛђЛїлхЛђл░ лил░ л│Лђл░лйлИЛєлхл╣ ЛЂл┤лхл╗л░л╗лИ лхл│лЙ Лѓлхл╝, л║лхл╝ лЙлй ЛЈл▓л╗ЛЈлхЛѓЛЂЛЈ ЛЂлхл╣ЛЄл░ЛЂ. лЪЛђлхл┤ЛЂлхл┤л░Лѓлхл╗лхл╝ л┐Лђл░л▓л╗лхлйлИЛЈ лХлИл╗лИЛЅлйлЙл│лЙ л║лЙлЙл┐лхЛђл░ЛѓлИл▓л░ ┬ФлЉлхЛЂЛѓ лњлхл╣┬╗, лЙЛЂлйлЙл▓л░Лѓлхл╗лхл╝ лИ л┐ЛђлхлилИл┤лхлйЛѓлЙл╝ л╝лхлХл┤ЛЃлйл░ЛђлЙл┤лйлЙл╣ л║лЙл╝л┐л░лйлИлИ ┬ФLife is Good┬╗, л░ Лѓл░л║лХлх лЙЛЂлйлЙл▓л░Лѓлхл╗лхл╝ люлхлХл┤ЛЃлйл░ЛђлЙл┤лйлЙл╣ л▒лИлилйлхЛЂ л░л║л░л┤лхл╝лИлИ ┬ФIBA┬╗. \r\n \r\nлалЙл╝л░лй лњл░ЛЂлИл╗лхлйл║лЙ Рђћ л┐л░ЛѓЛђлИлЙЛѓ ЛЂл▓лЙлхл╣ ЛђлЙл┤лИлйЛІ, лйлх Лђл░ли л▓ЛІЛЂл║л░лиЛІл▓л░л╗ЛЂЛЈ, ЛЄЛѓлЙ л┤л╗ЛЈ лйлхл│лЙ лЙЛЄлхлйЛї л▓л░лХлйлЙ, ЛЄЛѓлЙл▒ЛІ л╝лЙл╗лЙл┤ЛІлх л╗Лјл┤лИ лйлх ЛЃлхлилХл░л╗лИ лИли лйл░Лѕлхл╣ ЛЂЛѓЛђл░лйЛІ. лълй ЛЂЛЄлИЛѓл░лхЛѓ, ЛЄЛѓлЙ лхЛЂл╗лИ ЛЂлйл░л▒л┤лИЛѓЛї л╝лЙл╗лЙл┤ЛІЛЁ л║л▓л░л╗лИЛёлИЛєлИЛђлЙл▓л░лйлйЛІЛЁ ЛЂл┐лхЛєлИл░л╗лИЛЂЛѓлЙл▓ ЛЂлЙл▒ЛЂЛѓл▓лхлйлйлЙл╣ л║л▓л░ЛђЛѓлИЛђлЙл╣ лИ ЛЁлЙЛђлЙЛѕлхл╣ Лђл░л▒лЙЛѓлЙл╣, лЙлйлИ лйлх л▒ЛЃл┤ЛЃЛѓ л┤ЛЃл╝л░ЛѓЛї лЙл▒ ЛЇл╝лИл│Лђл░ЛєлИлИ л▓ л┤ЛђЛЃл│ЛЃЛј ЛЂЛѓЛђл░лйЛЃ лил░ л╗ЛЃЛЄЛѕлхл╣ лХлИлилйЛїЛј. лЪлЙЛЇЛѓлЙл╝ЛЃ ЛЃ лйлхл│лЙ л▓лЙлилйлИл║л╗л░ лИл┤лхЛЈ лХлИл╗лИЛЅлйлЙл│лЙ л║лЙлЙл┐лхЛђл░ЛѓлИл▓л░. \r\n \r\nлўЛЂл┐лЙл╗ЛїлиЛЃЛЈ л╝лйлЙл│лЙл╗лхЛѓлйлИл╣ лЙл┐ЛІЛѓ л▓ ЛЂЛёлхЛђлх л║лЙлйЛЂл░л╗ЛѓлИлйл│л░, л┤лЙл▒лИл▓ЛѕлИЛЂЛї ЛёлИлйл░лйЛЂлЙл▓лЙл│лЙ ЛЃЛЂл┐лхЛЁл░, л░ Лѓл░л║лХлх лИлиЛЃЛЄлИл▓ л░л║ЛѓЛЃл░л╗ЛїлйЛІлх л┐ЛђлЙл▒л╗лхл╝ЛІ лйл░ЛЂлхл╗лхлйлИЛЈ, лЙлй лйл░ЛЄл░л╗ л┤ЛЃл╝л░ЛѓЛї лЙ л┐лЙл╗лхлилйлЙл╝ л┤л╗ЛЈ л╗Лјл┤лхл╣ л▒лИлилйлхЛЂлх. лалЙл╝л░лй л┐Лђлхл┤л╗лЙлХлИл╗ ЛЂл┐лЙЛЂлЙл▒ ЛЂл┤лхл╗л░ЛѓЛї лХлИл╗Лїлх л┤лЙЛЂЛѓЛЃл┐лйЛІл╝ ЛЄлхЛђлхли лХлИл╗лИЛЅлйЛЃЛј л║лЙлЙл┐лхЛђл░ЛєлИЛј. лЋл│лЙ лИл┤лхЛЈ л▓лЙл┐л╗лЙЛЅл░лхЛѓ л▓ ЛЂлхл▒ЛЈ ЛЇЛёЛёлхл║ЛѓлИл▓лйЛЃЛј л╝лЙл┤лхл╗Лї л┐ЛђлИлЙл▒ЛђлхЛѓлхлйлИЛЈ лХлИл╗ЛїЛЈ л┤л╗ЛЈ л╝лЙл╗лЙл┤ЛІЛЁ ЛЂлхл╝лхл╣, л┐лЙлил▓лЙл╗ЛЈЛјЛЅЛЃЛј ЛЂлЙЛЁЛђл░лйлИЛѓЛї ЛЄлхл╗лЙл▓лхЛЄлхЛЂл║лИл╣ л║л░л┐лИЛѓл░л╗ лИ л┐Лђлхл┤лЙЛѓл▓Лђл░ЛѓлИЛѓЛї лЙЛѓЛѓлЙл║ л╝лЙл╗лЙл┤ЛІЛЁ ЛЂл┐лхЛєлИл░л╗лИЛЂЛѓлЙл▓ лИли ЛЂЛѓЛђл░лйЛІ. \r\n \r\nлъл▒ЛЃЛЄл░ЛЈЛЂЛї лил░ л│Лђл░лйлИЛєлхл╣, лЙлй л┐лЙлйЛЈл╗ лЙл┤лйЛЃ л┐ЛђлЙЛЂЛѓЛЃЛј лИЛЂЛѓлИлйЛЃ Рђћ лИл┤лхЛЈ л┤лЙл╗лХлйл░ л▒ЛІЛѓЛї л┐ЛђлЙЛЂЛѓлЙл╣ лИ ЛЇЛёЛёлхл║ЛѓлИл▓лйлЙл╣. лълй лИлиЛЃЛЄлИл╗ лЙл┐ЛІЛѓ л┤ЛђЛЃл│лИЛЁ ЛЂЛѓЛђл░лй, лИ, лЙЛЂлйлЙл▓ЛІл▓л░ЛЈЛЂЛї лйл░ лйлхл╝лхЛєл║лИЛЁ л║лЙлЙл┐лхЛђл░ЛѓлИл▓л░ЛЁ, ЛђлхЛѕлИл╗ л▓лйлхл┤ЛђлИЛѓЛї лхлх лйл░ ЛЂл▓лЙлхл╣ ЛђлЙл┤лИлйлх. лќлИл╗лИЛЅлйЛІлх л║лЙлЙл┐лхЛђл░ЛѓлИл▓ЛІ л▓ лЊлхЛђл╝л░лйлИлИ л┐лЙл╗ЛїлиЛЃЛјЛѓЛЂЛЈ л▒лЙл╗ЛїЛѕлИл╝ ЛЂл┐ЛђлЙЛЂлЙл╝ лйл░ ЛђЛІлйл║лх, лЙЛЂЛѓл░л▓л░ЛЈЛЂЛї лЙл┤лйлИл╝ лИли ЛЂл░л╝ЛІЛЁ л┤лЙЛЂЛѓЛЃл┐лйЛІЛЁ ЛЂл┐лЙЛЂлЙл▒лЙл▓ л┐ЛђлИлЙл▒ЛђлхЛѓлхлйлИЛЈ л║л▓л░ЛђЛѓлИЛђЛІ. лъл┤лйлИл╝ ЛЂл╗лЙл▓лЙл╝, ЛЃЛЄл░ЛЂЛѓлИлх л▓ лХлИл╗лИЛЅлйлЙл╝ л║лЙлЙл┐лхЛђл░ЛѓлИл▓лх РђЊ ЛЇЛѓлЙ л┐лЙл║ЛЃл┐л║л░ ЛЂл▓лЙлхл│лЙ л┤лЙл╝л░ л▓ Лђл░ЛЂЛЂЛђлЙЛЄл║ЛЃ. лњЛЂлх л┐лЙЛѓлхлйЛєлИл░л╗ЛїлйЛІлх ЛЃЛЄл░ЛЂЛѓлйлИл║лИ л║лЙлЙл┐лхЛђл░ЛѓлИл▓л░ л▓лйлЙЛЂЛЈЛѓ ЛЂл▓лЙлИ ЛЂЛђлхл┤ЛЂЛѓл▓л░ лИ л┐ЛђлИлЙл▒ЛђлхЛѓл░ЛјЛѓ лХлИл╗Лїлх л┐лхЛђл▓лЙл╝ЛЃ л┐л░л╣ЛЅлИл║ЛЃ. лбЛЃЛѓ л┤лхл╣ЛЂЛѓл▓ЛЃлхЛѓ ┬ФЛЂл░Лђл░Лёл░лйлйлЙлх Лђл░л┤лИлЙ┬╗, л║л░лХл┤ЛІл╣ л┐л░л╣ЛЅлИл║ лХл┤лхЛѓ ЛЂл▓лЙлхл│лЙ ЛЄл░ЛЂл░ ┬ФX┬╗. лџл░лХл┤ЛІл╣ ЛЄлхл╗лЙл▓лхл║ л┐лЙл╝лЙл│л░лхЛѓ л┤ЛђЛЃл│ л┤ЛђЛЃл│ЛЃ. лџлЙлЙл┐лхЛђл░ЛѓлИл▓ лЙл▒Лілхл┤лИлйЛЈлхЛѓ л╗Лјл┤лхл╣ лИ л┐лЙл╝лЙл│л░лхЛѓ л┐Лђл░л▓лИл╗ЛїлйлЙ лЙЛђл│л░лйлИлилЙл▓л░ЛѓЛї ЛёлИлйл░лйЛЂЛІ. лЋЛЂл╗лИ л▓ЛІ ЛЁлЙЛѓлИЛѓлх л▓ЛЂЛѓЛЃл┐лИЛѓЛї л▓ лХлИл╗лИЛЅлйЛІл╣ л║лЙлЙл┐лхЛђл░ЛѓлИл▓, лйлх л┐ЛђлИл┤лхЛѓЛЂЛЈ ЛЂлЙл▒лИЛђл░ЛѓЛї л┤лЙл║ЛЃл╝лхлйЛѓЛІ лЙ л╝лхЛЂЛѓлх Лђл░л▒лЙЛѓЛІ лИл╗лИ ЛЂлЙЛЂЛѓлЙЛЈлйлИлИ л┤лЙЛЁлЙл┤лЙл▓. лћлЙЛЂЛѓл░ЛѓлЙЛЄлйлЙ лИл╝лхЛѓЛї ЛЂ ЛЂлЙл▒лЙл╣ л┐л░ЛЂл┐лЙЛђЛѓ лИ л▓лйлхЛЂЛѓлИ л┐лхЛђл▓ЛІл╣ л▓лилйлЙЛЂ. \r\n \r\nлћл╗ЛЈ лалЙл╝л░лйл░ лњлИл║ЛѓлЙЛђлЙл▓лИЛЄл░ лњл░ЛЂлИл╗лхлйл║лЙ л▓л░лХлйл░ л┐ЛђлЙлиЛђл░ЛЄлйлЙЛЂЛѓЛї л▓лхл┤лхлйлИЛЈ л▒лИлилйлхЛЂл░ лИ л┐лЙЛЂЛѓлЙЛЈлйлйлЙлх лЙл▒Лђл░лилЙл▓л░лйлИлх. лълй л┤лЙ ЛЂлИЛЁ л┐лЙЛђ л┐ЛђлЙл┤лЙл╗лХл░лхЛѓ ЛЃЛЄлИЛѓЛїЛЂЛЈ лйл░ Лђл░лил╗лИЛЄлйЛІЛЁ ЛёлИлйл░лйЛЂлЙл▓ЛІЛЁ л║ЛЃЛђЛЂл░ЛЁ. лЮлхЛЂл╝лЙЛѓЛђЛЈ лйл░ ЛЃлХлх л┐лЙл║лЙЛђлхлйлйЛІлх л▓лхЛђЛѕлИлйЛІ, лЙлй л▓ЛЂлхл│л┤л░ л│лЙл▓лЙЛђлИЛѓ, ЛЄЛѓлЙ лхЛЅлх лхЛЂЛѓЛї л║ ЛЄлхл╝ЛЃ ЛЂЛѓЛђлхл╝лИЛѓЛїЛЂЛЈ. лДЛѓлЙл▒ЛІ лИл┤ЛѓлИ л┤л░л╗ЛїЛѕлх, лйлхлЙл▒ЛЁлЙл┤лИл╝лЙ ЛЂлЙл▓лхЛђЛѕлхлйЛЂЛѓл▓лЙл▓л░ЛѓЛїЛЂЛЈ. лњЛЂлх, ЛЄЛѓлЙ л┐Лђлхл┤л╗л░л│л░лхЛѓ лалЙл╝л░лй, лЙЛЂлйлЙл▓л░лйлЙ лйл░ лхл│лЙ ЛЂлЙл▒ЛЂЛѓл▓лхлйлйлЙл╝ лЙл┐ЛІЛѓлх. лћл▓лх л│л╗л░л▓лйЛІлх лйлИЛѕлИ л┤л╗ЛЈ лйлхл│лЙ Рђћ ЛЇЛѓлЙ л▓лЙЛЂл┐лИЛѓл░лйлИлх ЛЃЛЂлхЛђл┤лИЛЈ лИ ЛѓЛђЛЃл┤лЙл╗Лјл▒лИЛЈ.', 'лалЙл╝л░лй лњлИл║ЛѓлЙЛђлЙл▓лИЛЄ лњл░ЛЂлИл╗лхлйл║лЙ Рђћ ЛЄлхл╗лЙл▓лхл║ ЛЂ л▒лЙл╗ЛїЛѕлЙл╣ л▒ЛЃл║л▓ЛІ! \r\n<a href=http://ulgrad.ru/?p=184592>лалЙл╝л░лй лњл░ЛЂлИл╗лхлйл║лЙ ЛЃл│лЙл╗лЙл▓лйлЙлх л┤лхл╗лЙ </a> \r\nлБ лалЙл╝л░лйл░ лњл░ЛЂлИл╗лхлйл║лЙ л▒лИлЙл│Лђл░ЛёлИЛЈ лйлхл┐ЛђлЙЛЂЛѓл░ЛЈ. лълй л▓ЛІЛђлЙЛЂ л▓ ЛЂлхл╝Лїлх л▓лЙлхлйлйЛІЛЁ лИ ЛђлхЛѕлИл╗ л┐лЙл╣ЛѓлИ л┐лЙ ЛЂЛѓлЙл┐л░л╝ лЙЛѓЛєл░. лњ 1990 л│лЙл┤ЛЃ лалЙл╝л░лй лњлИл║ЛѓлЙЛђлЙл▓лИЛЄ лил░л║лЙлйЛЄлИл╗ лњЛІЛЂЛѕлхлх лњлЙлхлйлйлЙлх лцлИлйл░лйЛЂлЙл▓лЙлх лБЛЄлИл╗лИЛЅлх л▓ л»ЛђлЙЛЂл╗л░л▓л╗лх, л░ л┐лЙЛѓлЙл╝ л▓лЙЛЂлхл╝Лї л╗лхЛѓ л┐лЙЛЂл╗лх лхл│лЙ лЙл║лЙлйЛЄл░лйлИЛЈ л┐ЛђлЙл▓ЛЉл╗ л▓ л▓лЙлЙЛђЛЃлХлхлйлйЛІЛЁ ЛЂлИл╗л░ЛЁ. лњлЙлхлйлйЛЃЛј ЛЂл╗ЛЃлХл▒ЛЃ лалЙл╝л░лй лњл░ЛЂлИл╗лхлйл║лЙ л▓ЛІл▒Лђл░л╗ л┐лЙ ЛЂлЙл▒ЛЂЛѓл▓лхлйлйлЙл╝ЛЃ лХлхл╗л░лйлИЛј, лйлЙ ЛЂлИл╗ЛїлйлЙ л┐лЙл▓л╗лИЛЈл╗лЙ ЛѓлЙ, ЛЄЛѓлЙ ЛђлЙл┤лИЛѓлхл╗лИ л▒ЛІл╗лИ л▓лЙлхлйлйлЙЛЂл╗ЛЃлХл░ЛЅлИл╝лИ. лњЛЂлх л┤лхЛѓЛЂЛѓл▓лЙ лЙлй лхлил┤лИл╗ л┐лЙ л▓лЙлхлйлйЛІл╝ л│лЙЛђлЙл┤л║л░л╝, л▓ЛЂлх лхл│лЙ л┤лхЛѓЛЂЛѓл▓лЙ лИ лЙЛѓЛђлЙЛЄлхЛЂЛѓл▓лЙ л▒ЛІл╗лИ ЛЂл▓ЛЈлил░лйЛІ ЛЂ л░Лђл╝лИлхл╣. лћлЙл╗л│лИлх л│лЙл┤ЛІ лЙлй ЛЂл╗ЛЃлХлИл╗ лйл░ЛЄл░л╗ЛїлйлИл║лЙл╝ ЛёлИлйл░лйЛЂлЙл▓лЙл╣ ЛЂл╗ЛЃлХл▒ЛІ лйл░ лЏлхлйлИлйл│Лђл░л┤ЛЂл║лЙл╣ л▓лЙлхлйлйлЙ-л╝лЙЛђЛЂл║лЙл╣ л▒л░лилх. лњ 1998 л│лЙл┤ЛЃ л▓ лил▓л░лйлИлИ л║л░л┐лИЛѓл░лйл░ ЛЃЛѕлхл╗ л▓ лил░л┐л░ЛЂ. \r\nлЪлЙлилХлх л┐лЙл╗ЛЃЛЄлИл╗ л┐ЛђлЙЛёлхЛЂЛЂлИЛј ЛЇл║лЙлйлЙл╝лИЛЂЛѓл░ лИ ЛђлхЛѕлИл╗ лЙл║ЛЃлйЛЃЛѓЛїЛЂЛЈ ЛЂ л│лЙл╗лЙл▓лЙл╣ л▓ ЛЂЛёлхЛђЛЃ ЛёлИлйл░лйЛЂлИЛђлЙл▓л░лйлИЛЈ. лЪлЙЛЂлхЛѓлИл╗ л╝лйлЙл│лЙ л║ЛЃЛђЛЂлЙл▓ л▓ лњлхлйл│ЛђлИлИ, лЉЛЃл┤л░л┐лхЛѕЛѓлх, лЪл░ЛђлИлХлх, лел▓лхл╣Лєл░ЛђлИлИ, лйл░ лџлИл┐Лђлх, л▓ лљл▓ЛЂЛѓЛђлИлИ, лАлелљ. лБл╝лхлйлИлх л┤лЙЛЂЛѓлИл│л░ЛѓЛї Лєлхл╗лхл╣ лИ л▒лхлиЛЃл┐ЛђлхЛЄлйл░ЛЈ л║л░ЛђЛїлхЛђл░ лил░ л│Лђл░лйлИЛєлхл╣ ЛЂл┤лхл╗л░л╗лИ лхл│лЙ Лѓлхл╝, л║лхл╝ лЙлй ЛЈл▓л╗ЛЈлхЛѓЛЂЛЈ ЛЂлхл╣ЛЄл░ЛЂ. лЪЛђлхл┤ЛЂлхл┤л░Лѓлхл╗лхл╝ л┐Лђл░л▓л╗лхлйлИЛЈ лХлИл╗лИЛЅлйлЙл│лЙ л║лЙлЙл┐лхЛђл░ЛѓлИл▓л░ ┬ФлЉлхЛЂЛѓ лњлхл╣┬╗, лЙЛЂлйлЙл▓л░Лѓлхл╗лхл╝ лИ л┐ЛђлхлилИл┤лхлйЛѓлЙл╝ л╝лхлХл┤ЛЃлйл░ЛђлЙл┤лйлЙл╣ л║лЙл╝л┐л░лйлИлИ ┬ФLife is Good┬╗, л░ Лѓл░л║лХлх лЙЛЂлйлЙл▓л░Лѓлхл╗лхл╝ люлхлХл┤ЛЃлйл░ЛђлЙл┤лйлЙл╣ л▒лИлилйлхЛЂ л░л║л░л┤лхл╝лИлИ ┬ФIBA┬╗. \r\n \r\nлалЙл╝л░лй лњл░ЛЂлИл╗лхлйл║лЙ Рђћ л┐л░ЛѓЛђлИлЙЛѓ ЛЂл▓лЙлхл╣ ЛђлЙл┤лИлйЛІ, лйлх Лђл░ли л▓ЛІЛЂл║л░лиЛІл▓л░л╗ЛЂЛЈ, ЛЄЛѓлЙ л┤л╗ЛЈ лйлхл│лЙ лЙЛЄлхлйЛї л▓л░лХлйлЙ, ЛЄЛѓлЙл▒ЛІ л╝лЙл╗лЙл┤ЛІлх л╗Лјл┤лИ лйлх ЛЃлхлилХл░л╗лИ лИли лйл░Лѕлхл╣ ЛЂЛѓЛђл░лйЛІ. лълй ЛЂЛЄлИЛѓл░лхЛѓ, ЛЄЛѓлЙ лхЛЂл╗лИ ЛЂлйл░л▒л┤лИЛѓЛї л╝лЙл╗лЙл┤ЛІЛЁ л║л▓л░л╗лИЛёлИЛєлИЛђлЙл▓л░лйлйЛІЛЁ ЛЂл┐лхЛєлИл░л╗лИЛЂЛѓлЙл▓ ЛЂлЙл▒ЛЂЛѓл▓лхлйлйлЙл╣ л║л▓л░ЛђЛѓлИЛђлЙл╣ лИ ЛЁлЙЛђлЙЛѕлхл╣ Лђл░л▒лЙЛѓлЙл╣, лЙлйлИ лйлх л▒ЛЃл┤ЛЃЛѓ л┤ЛЃл╝л░ЛѓЛї лЙл▒ ЛЇл╝лИл│Лђл░ЛєлИлИ л▓ л┤ЛђЛЃл│ЛЃЛј ЛЂЛѓЛђл░лйЛЃ лил░ л╗ЛЃЛЄЛѕлхл╣ лХлИлилйЛїЛј. лЪлЙЛЇЛѓлЙл╝ЛЃ ЛЃ лйлхл│лЙ л▓лЙлилйлИл║л╗л░ лИл┤лхЛЈ лХлИл╗лИЛЅлйлЙл│лЙ л║лЙлЙл┐лхЛђл░ЛѓлИл▓л░. \r\n \r\nлўЛЂл┐лЙл╗ЛїлиЛЃЛЈ л╝лйлЙл│лЙл╗лхЛѓлйлИл╣ лЙл┐ЛІЛѓ л▓ ЛЂЛёлхЛђлх л║лЙлйЛЂл░л╗ЛѓлИлйл│л░, л┤лЙл▒лИл▓ЛѕлИЛЂЛї ЛёлИлйл░лйЛЂлЙл▓лЙл│лЙ ЛЃЛЂл┐лхЛЁл░, л░ Лѓл░л║лХлх лИлиЛЃЛЄлИл▓ л░л║ЛѓЛЃл░л╗ЛїлйЛІлх л┐ЛђлЙл▒л╗лхл╝ЛІ лйл░ЛЂлхл╗лхлйлИЛЈ, лЙлй лйл░ЛЄл░л╗ л┤ЛЃл╝л░ЛѓЛї лЙ л┐лЙл╗лхлилйлЙл╝ л┤л╗ЛЈ л╗Лјл┤лхл╣ л▒лИлилйлхЛЂлх. лалЙл╝л░лй л┐Лђлхл┤л╗лЙлХлИл╗ ЛЂл┐лЙЛЂлЙл▒ ЛЂл┤лхл╗л░ЛѓЛї лХлИл╗Лїлх л┤лЙЛЂЛѓЛЃл┐лйЛІл╝ ЛЄлхЛђлхли лХлИл╗лИЛЅлйЛЃЛј л║лЙлЙл┐лхЛђл░ЛєлИЛј. лЋл│лЙ лИл┤лхЛЈ л▓лЙл┐л╗лЙЛЅл░лхЛѓ л▓ ЛЂлхл▒ЛЈ ЛЇЛёЛёлхл║ЛѓлИл▓лйЛЃЛј л╝лЙл┤лхл╗Лї л┐ЛђлИлЙл▒ЛђлхЛѓлхлйлИЛЈ лХлИл╗ЛїЛЈ л┤л╗ЛЈ л╝лЙл╗лЙл┤ЛІЛЁ ЛЂлхл╝лхл╣, л┐лЙлил▓лЙл╗ЛЈЛјЛЅЛЃЛј ЛЂлЙЛЁЛђл░лйлИЛѓЛї ЛЄлхл╗лЙл▓лхЛЄлхЛЂл║лИл╣ л║л░л┐лИЛѓл░л╗ лИ л┐Лђлхл┤лЙЛѓл▓Лђл░ЛѓлИЛѓЛї лЙЛѓЛѓлЙл║ л╝лЙл╗лЙл┤ЛІЛЁ ЛЂл┐лхЛєлИл░л╗лИЛЂЛѓлЙл▓ лИли ЛЂЛѓЛђл░лйЛІ. \r\n \r\nлъл▒ЛЃЛЄл░ЛЈЛЂЛї лил░ л│Лђл░лйлИЛєлхл╣, лЙлй л┐лЙлйЛЈл╗ лЙл┤лйЛЃ л┐ЛђлЙЛЂЛѓЛЃЛј лИЛЂЛѓлИлйЛЃ Рђћ лИл┤лхЛЈ л┤лЙл╗лХлйл░ л▒ЛІЛѓЛї л┐ЛђлЙЛЂЛѓлЙл╣ лИ ЛЇЛёЛёлхл║ЛѓлИл▓лйлЙл╣. лълй лИлиЛЃЛЄлИл╗ лЙл┐ЛІЛѓ л┤ЛђЛЃл│лИЛЁ ЛЂЛѓЛђл░лй, лИ, лЙЛЂлйлЙл▓ЛІл▓л░ЛЈЛЂЛї лйл░ лйлхл╝лхЛєл║лИЛЁ л║лЙлЙл┐лхЛђл░ЛѓлИл▓л░ЛЁ, ЛђлхЛѕлИл╗ л▓лйлхл┤ЛђлИЛѓЛї лхлх лйл░ ЛЂл▓лЙлхл╣ ЛђлЙл┤лИлйлх. лќлИл╗лИЛЅлйЛІлх л║лЙлЙл┐лхЛђл░ЛѓлИл▓ЛІ л▓ лЊлхЛђл╝л░лйлИлИ л┐лЙл╗ЛїлиЛЃЛјЛѓЛЂЛЈ л▒лЙл╗ЛїЛѕлИл╝ ЛЂл┐ЛђлЙЛЂлЙл╝ лйл░ ЛђЛІлйл║лх, лЙЛЂЛѓл░л▓л░ЛЈЛЂЛї лЙл┤лйлИл╝ лИли ЛЂл░л╝ЛІЛЁ л┤лЙЛЂЛѓЛЃл┐лйЛІЛЁ ЛЂл┐лЙЛЂлЙл▒лЙл▓ л┐ЛђлИлЙл▒ЛђлхЛѓлхлйлИЛЈ л║л▓л░ЛђЛѓлИЛђЛІ. лъл┤лйлИл╝ ЛЂл╗лЙл▓лЙл╝, ЛЃЛЄл░ЛЂЛѓлИлх л▓ лХлИл╗лИЛЅлйлЙл╝ л║лЙлЙл┐лхЛђл░ЛѓлИл▓лх РђЊ ЛЇЛѓлЙ л┐лЙл║ЛЃл┐л║л░ ЛЂл▓лЙлхл│лЙ л┤лЙл╝л░ л▓ Лђл░ЛЂЛЂЛђлЙЛЄл║ЛЃ. лњЛЂлх л┐лЙЛѓлхлйЛєлИл░л╗ЛїлйЛІлх ЛЃЛЄл░ЛЂЛѓлйлИл║лИ л║лЙлЙл┐лхЛђл░ЛѓлИл▓л░ л▓лйлЙЛЂЛЈЛѓ ЛЂл▓лЙлИ ЛЂЛђлхл┤ЛЂЛѓл▓л░ лИ л┐ЛђлИлЙл▒ЛђлхЛѓл░ЛјЛѓ лХлИл╗Лїлх л┐лхЛђл▓лЙл╝ЛЃ л┐л░л╣ЛЅлИл║ЛЃ. лбЛЃЛѓ л┤лхл╣ЛЂЛѓл▓ЛЃлхЛѓ ┬ФЛЂл░Лђл░Лёл░лйлйлЙлх Лђл░л┤лИлЙ┬╗, л║л░лХл┤ЛІл╣ л┐л░л╣ЛЅлИл║ лХл┤лхЛѓ ЛЂл▓лЙлхл│лЙ ЛЄл░ЛЂл░ ┬ФX┬╗. лџл░лХл┤ЛІл╣ ЛЄлхл╗лЙл▓лхл║ л┐лЙл╝лЙл│л░лхЛѓ л┤ЛђЛЃл│ л┤ЛђЛЃл│ЛЃ. лџлЙлЙл┐лхЛђл░ЛѓлИл▓ лЙл▒Лілхл┤лИлйЛЈлхЛѓ л╗Лјл┤лхл╣ лИ л┐лЙл╝лЙл│л░лхЛѓ л┐Лђл░л▓лИл╗ЛїлйлЙ лЙЛђл│л░лйлИлилЙл▓л░ЛѓЛї ЛёлИлйл░лйЛЂЛІ. лЋЛЂл╗лИ л▓ЛІ ЛЁлЙЛѓлИЛѓлх л▓ЛЂЛѓЛЃл┐лИЛѓЛї л▓ лХлИл╗лИЛЅлйЛІл╣ л║лЙлЙл┐лхЛђл░ЛѓлИл▓, лйлх л┐ЛђлИл┤лхЛѓЛЂЛЈ ЛЂлЙл▒лИЛђл░ЛѓЛї л┤лЙл║ЛЃл╝лхлйЛѓЛІ лЙ л╝лхЛЂЛѓлх Лђл░л▒лЙЛѓЛІ лИл╗лИ ЛЂлЙЛЂЛѓлЙЛЈлйлИлИ л┤лЙЛЁлЙл┤лЙл▓. лћлЙЛЂЛѓл░ЛѓлЙЛЄлйлЙ лИл╝лхЛѓЛї ЛЂ ЛЂлЙл▒лЙл╣ л┐л░ЛЂл┐лЙЛђЛѓ лИ л▓лйлхЛЂЛѓлИ л┐лхЛђл▓ЛІл╣ л▓лилйлЙЛЂ. \r\n \r\nлћл╗ЛЈ лалЙл╝л░лйл░ лњлИл║ЛѓлЙЛђлЙл▓лИЛЄл░ лњл░ЛЂлИл╗лхлйл║лЙ л▓л░лХлйл░ л┐ЛђлЙлиЛђл░ЛЄлйлЙЛЂЛѓЛї л▓лхл┤лхлйлИЛЈ л▒лИлилйлхЛЂл░ лИ л┐лЙЛЂЛѓлЙЛЈлйлйлЙлх лЙл▒Лђл░лилЙл▓л░лйлИлх. лълй л┤лЙ ЛЂлИЛЁ л┐лЙЛђ л┐ЛђлЙл┤лЙл╗лХл░лхЛѓ ЛЃЛЄлИЛѓЛїЛЂЛЈ лйл░ Лђл░лил╗лИЛЄлйЛІЛЁ ЛёлИлйл░лйЛЂлЙл▓ЛІЛЁ л║ЛЃЛђЛЂл░ЛЁ. лЮлхЛЂл╝лЙЛѓЛђЛЈ лйл░ ЛЃлХлх л┐лЙл║лЙЛђлхлйлйЛІлх л▓лхЛђЛѕлИлйЛІ, лЙлй л▓ЛЂлхл│л┤л░ л│лЙл▓лЙЛђлИЛѓ, ЛЄЛѓлЙ лхЛЅлх лхЛЂЛѓЛї л║ ЛЄлхл╝ЛЃ ЛЂЛѓЛђлхл╝лИЛѓЛїЛЂЛЈ. лДЛѓлЙл▒ЛІ лИл┤ЛѓлИ л┤л░л╗ЛїЛѕлх, лйлхлЙл▒ЛЁлЙл┤лИл╝лЙ ЛЂлЙл▓лхЛђЛѕлхлйЛЂЛѓл▓лЙл▓л░ЛѓЛїЛЂЛЈ. лњЛЂлх, ЛЄЛѓлЙ л┐Лђлхл┤л╗л░л│л░лхЛѓ лалЙл╝л░лй, лЙЛЂлйлЙл▓л░лйлЙ лйл░ лхл│лЙ ЛЂлЙл▒ЛЂЛѓл▓лхлйлйлЙл╝ лЙл┐ЛІЛѓлх. лћл▓лх л│л╗л░л▓лйЛІлх лйлИЛѕлИ л┤л╗ЛЈ лйлхл│лЙ Рђћ ЛЇЛѓлЙ л▓лЙЛЂл┐лИЛѓл░лйлИлх ЛЃЛЂлхЛђл┤лИЛЈ лИ ЛѓЛђЛЃл┤лЙл╗Лјл▒лИЛЈ.', 'User', 'User', '2024-03-26 13:26:10')
